import React, { Component } from 'react';
import './App.css';
 var NumberOfColumns = 6;
 var NumberOfRows = 6;
var cellStyle={
  background:"red",
  padding:2
}
class Circle extends Component{

  render(){
   var color = this.props.color
    var style={
      width:50,
      height:50,
      borderRadius:50,
      color:"black",
      background:color,
    }
    return( <div style={style}>{this.props.col},{this.props.row}</div>)
  }
}
class Cell extends Component{
  // constructor(props){
  //   super(props){}

  // }
  render(){
    return(<div style={cellStyle}  onClick={this.props.clickHandling(this.props.row,this.props.col)}>
  <Circle col={this.props.col} row={this.props.row} color={this.props.color}/>
    </div>)
  }
}
class Row extends Component{
render(){
  var row1=[]
  for (let i = 0 ; i < NumberOfColumns ; i++){row1.push(<Cell col={i} key={i} row={this.props.row} color={this.props.color} clickHandling={this.props.clickHandling}/>)}

  return(<div style={{display:"flex"}} >
    {row1}
  </div> )
}
}
class Bord extends Component{
  constructor(props){
    super(props);
    var columns =[]
    for( let i = 0 ; i < NumberOfColumns ; i++ ){columns[i] = 0}
    var stateOfCells = []
    for( let i = 0 ; i < NumberOfRows    ; i++ ){stateOfCells.push(columns); }
    this.state={turn:"true",stateOfCells:stateOfCells};


  }
  changeState =(row,col)=> {
    var tempArr = this.state.stateOfCells;
    tempArr[row,col]=1
    this.setState({statOfCells:tempArr})
        }
  render(){
    var cols=[]
    for(let i = 0 ; i < NumberOfRows ; i++){cols.push(<Row key={i} row={i} color={this.state.color} clickHandling={this.changeState.bind(this)} />)}
    return(
      <div >
        {cols}
      </div>
    )
  }
}


class App extends Component {
  render() {
    return (
      <div className="App">
        <header className="App-header">
        <Bord  />
        </header>
      </div>
    );
  }
}

export default App;
