import React, { Component } from 'react';
import './App.css';
var style={
  width:50,
  height:50,
  borderRadius:50,
  color:"black",
  background:"yellow",
}
var cellStyle={
  background:"red",
  padding:2
}
class Circle extends Component{
  render(){
    return( <div style={style}>{this.props.col},{this.props.row}</div>)
  }
}
class Cell extends Component{
  // constructor(props){
  //   super(props){}

  // }
  render(){
    return(<div style={cellStyle} col="this.props.col" row="this.props.row" onClick={this.props.clickHandling}>
  <Circle col={this.props.col} row={this.props.row}/>
    </div>)
  }
}
class Row extends Component{
render(){
  var row1=[]
  for (let i = 0 ; i < 5 ; i++){row1.push(<Cell col={i} key={i} row={this.props.row}/>)}

  return(<div style={{display:"flex"}} row={this.props.row} onClick={this.props.clickHandling}>
    {row1}
  </div> )
}
}
class Bord extends Component{


  render(){
    var cols=[]
    for(let i = 0 ; i < 5 ; i++){cols.push(<Row key={i} row={i}/>)}
    return(
      <div >
        {cols}
      </div>
    )
  }
}


class App extends Component {
  render() {
    return (
      <div className="App">
        <header className="App-header">
        <Bord  />
        </header>
      </div>
    );
  }
}

export default App;
