import React, { Component } from 'react';
import './App.css';
 var NumberOfColumns = 6;
 var NumberOfRows = 6;
var cellStyle={
  background:"red",
  padding:2
}
class Circle extends Component{

  render(){
    var color = "yellow"
    var myrow = this.props.row;
    var mycol = this.props.col;
    var mycolor=this.props.color[myrow][mycol];

    if ( mycolor === 1){color="green"}
    if(mycolor == 2 ){color="blue"}
    // var mycolor=this.props.color[this.props.row][this.props.col];
    // console.log("row is:" +this.props.row  + ".///.col is ....." + this.props.col + "....mycolor:" + this.mycolor)
  //  var color = this.props.color
    var style={
      width:50,
      height:50,
      borderRadius:50,
      color:"black",
      background:color,
    }
    return( <div style={style} onClick={(e) => this.props.clickHandling(this.props.row,this.props.col)}>{this.props.col},{this.props.row}</div>)
  }
}
class Cell extends Component{
  // constructor(props){
  //   super(props){}

  // }
  render(){
    return(<div style={cellStyle} >
  <Circle col={this.props.col} row={this.props.row} color={this.props.color}  clickHandling={this.props.clickHandling}/>
    </div>)
  }
}
class Row extends Component{
render(){
  var row1=[]
  for (let i = 0 ; i < NumberOfColumns ; i++){row1.push(<Cell col={i} key={i} row={this.props.row} color={this.props.color} clickHandling={this.props.clickHandling}/>)}

  return(<div style={{display:"flex"}} >
    {row1}
  </div> )
}
}
class Bord extends Component{
    constructor(props){
    super(props);
    var stateCells =[];
    for( let i = 0 ; i < NumberOfColumns ; i++ ){
       stateCells.push([]);
      for( let j = 0 ; j < NumberOfRows    ; j++ ){ stateCells[i].push(0)} };

    this.state={turn:"true",stateOfCells:stateCells,color:"yellow"};

  }

  resetFunction =()=>{
    var stateCells =[];
    for( let i = 0 ; i < NumberOfColumns ; i++ ){
       stateCells.push([]);
      for( let j = 0 ; j < NumberOfRows    ; j++ ){ stateCells[i].push(0)} };

    this.state={turn:"true",stateOfCells:stateCells,color:"yellow"};
    }


  changeState =(row,col)=> {
    var myTurn = this.state.turn ? false : true;
    var tempArr = this.state.stateOfCells;
    if (myTurn){ tempArr[row][col]=1;}
     else{tempArr[row][col]=2}
    this.setState({statOfCells:tempArr,turn:myTurn});
    console.log(this.state.turn)

        }
  render(){
    var cols=[[],[]]
    for(let i = 0 ; i < NumberOfRows ; i++){cols.push(<Row key={i} row={i} color={this.state.stateOfCells} clickHandling={this.changeState} />)}
    return(
      <div >
        {cols}
        <h5> now player no </h5><h2> {this.state.turn? "blue" : "green"}</h2>
        <button onclick={this.resetFunction}> Reset</button>
      </div>
    )
  }
}


class App extends Component {
  render() {
    return (
      <div className="App">
        <header className="App-header">
        <Bord  />
        </header>
      </div>
    );
  }
}

export default App;
